import React, { Component } from "react";
import "./App.css";
import MovieRow from "./movieRow.js";
import $ from "jquery";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    // const movies = [
    //   { id: 0, poster_src: "", title: "movie1", overview: "first overview" },
    //   { id: 1, poster_src: "", title: "movie2", overview: "second overview" }
    // ];

    // let movieRows = movies.map(movie => {
    //   const movieRow = <MovieRow movie={movie} />;
    //   return movieRow;
    // });

    // this.state = { rows: movieRows };

    this.performSearch();
  }

  performSearch = () => {
    const urlString =
      "https://api.themoviedb.org/3/search/movie?api_key=1b5adf76a72a13bad99b8fc0c68cb085&query=naruto";
    $.ajax({
      url: urlString,
      success: searchResult => {
        // console.log("Fetch data successfully");
        const results = searchResult.results;
        results.forEach(movie => {
          console.log(movie.title);
        });
      },
      error: (xhr, status, err) => {
        console.log("Failed to fetch data");
      }
    });
  };

  render() {
    return (
      <div>
        <table className="titleBar">
          <tbody>
            <tr>
              <td>
                <img width="50" height="50" src="./search.svg" alt="" />
              </td>
              <td width="10" />
              <td>
                <h1>Movie Digger - The MoviesDB Search</h1>
              </td>
            </tr>
          </tbody>
        </table>

        <input className="searchBar" placeholder="Enter movie name" />

        {this.state.rows}
      </div>
    );
  }
}

export default App;
